import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';
import { getAllProblems } from '../actions/index';

class Problem extends Component {

    componentDidMount() {
        this.getProblems();
    }

    getProblems() {
         this.props.getAllProblems();
         console.log('posts: ', this.props.posts)
    }
    render() {    
        return this.props.posts.length ? (
            <Fragment>
                <ul>
                {
                    this.props.posts.map(postData => {
                        return (
                            <li key={postData.data[1].id}>
                                <h3>{postData.data[0].title}</h3>
                                <p>{postData.data[0].description}</p>
                            </li>
                        )
                    })
                }
                </ul>
            </Fragment>
        ) : <h1>Loading...</h1>
    }
}

function mapStateToProps(state) {
    return {
        posts: state.posts
    }
}
export default connect(mapStateToProps, { getAllProblems })(Problem);
