import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';
import { getAllProblems } from '../actions/index';

class Problem extends Component {

    componentDidMount() {
        this.getProblems();
    }

    getProblems() {
         this.props.getAllProblems();
         console.log('posts: ', this.props.posts[0])
    }
    render() {    
        return this.props.posts.length ? (
            <Fragment>
                <ul>
                {
                    this.props.posts.forEach( postData => {
                        console.log('post: ', postData.data)
                        postData.data.map(post => {
                            console.log('real post: ', post)
                            return (
                                <li key={postData.data[0].id}>
                                    <h3>{postData.data[1].title}</h3>
                                    <p>{postData.data[1].description}</p>
                                </li>
                        )
                        })

                    })
                }
                </ul>
            </Fragment>
        ) : <h1>Loading...</h1>
    }
}

function mapStateToProps(state) {
    return {
        posts: state.posts
    }
}
export default connect(mapStateToProps, { getAllProblems })(Problem);
