import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';
import { getAllProblems } from '../actions/index';

class Problem extends Component {
    constructor(props) {
        super(props)

        this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount() {
        this.getProblems();
    }

    handleClick() {
        this.getProblems();
    }

    getProblems() {
         this.props.getAllProblems();
         console.log('posts: ', this.props.posts)
    }
    render() {    
        return this.props.posts.length ? (
            <Fragment>
                <button onClick={this.handleClick}>Refresh</button>
                <ul>
                {
                    this.props.posts.map(postData => {
                        return <h1>{postData.data[0].title}</h1>
                    })
                }
                </ul>
            </Fragment>
        ) : <h1>Loading...</h1>
    }
}

function mapStateToProps(state) {
    return {
        posts: state.posts
    }
}
export default connect(mapStateToProps, { getAllProblems })(Problem);
