import React, { Component } from 'react';
import { Button, TextField } from '@material-ui/core';
import API from '../utils/API';

class PostProblem extends Component {
    constructor(props) {
        super(props)

        this.handleClick = this.handleClick.bind(this);
        this.state = {
            title: ""
        }
    }

    handleClick() {
        const problemTitle = document.querySelector("#problemTitle").value || "No title provided";
        console.log("problem title : " + problemTitle)
        this.setState({
            title: problemTitle
        })
        API.createNewProblem().then().catch();
    }

    render() {
        return (
            <div>
                <TextField id="problemTitle" />

                <Button size="small" onClick={this.handleClick}>
                    Post
                </Button>
            </div>
        )
    }
}

export default PostProblem;
