import React, { Component, Fragment } from 'react';
import { Paper, Typography } from '@material-ui/core';
import { connect } from 'react-redux';
import { getAllProblems } from '../actions/index';

class Problem extends Component {

    componentDidMount() {
        this.props.getAllProblems();
    }
    render() {
        const posts = this.props.posts;
        console.log('posts: ', posts)
        for (let index = 0; index < posts.length; index++) {
            console.log("index: ", posts[index]);
            console.log("data: ", posts[index].data);
            posts[index].data.map(post => (
                console.log('title: ', post.title)
            ))

        }
        // posts.data.map(post => {
        //     console.log('title: ', post.title)
        //     console.log('description: ', posts.description);
        // })
        return (
            <Paper component="div">
                <Typography component="h3">
                    {this.props.problemTitle}
                </Typography>
                {
                    posts.forEach(item => {
                        item.data.map(post => {
                            return <Fragment>
                                <div key={post.id}>{post.title}</div>
                                <div key={post.id}>{post.description}</div>
                            </Fragment>
                        })
                    })
                }
            </Paper>
        )
    }
}

function mapStateToProps(state) {
    return {
        posts: state.posts
    }
}
export default connect(mapStateToProps, { getAllProblems })(Problem);
